import { useState } from "react";

export default function ProjectForm() {
  const [projectName, setProjectName] = useState("");
  const [projectDesc, setProjectDesc] = useState("");
  const [projectLink, setProjectLink] = useState("");

  const handleSubmit = () => {
    const result = localStorage.getItem("projects");
    if (result === null) {
      localStorage.setItem(
        "projects",
        JSON.stringify([{ projectName, projectDesc, projectLink }])
      );
    } else {
      const res = JSON.parse(result);
      localStorage.setItem(
        "projects",
        JSON.stringify([...res, { projectName, projectDesc, projectLink }])
      );
    }
  };

  const handleChange = (e) => {
    if (e.id === "ProjectLink") {
      setProjectLink(e.target.value);
    } else if (e.id === "ProjectDesc") {
      setProjectDesc(e.target.value);
    } else {
      setProjectName(e.target.value);
    }
  };
  return (
    <div className="box2">
      <div className="formBox">
        <h2 className="formTitle">Add Project</h2>
        <div className="underline">--</div>
        <form onSubmit={handleSubmit}>
          <label htmlFor="projectName">Project Name</label>
          <br />
          <input
            value={projectName}
            id="projectName"
            type="text"
            onChange={handleChange}
          />
          <br />
          <label htmlFor="projectLink">Project Link</label>
          <br />
          <input id="projectLink" type="text" onChange={handleChange} />
          <br />
          <label htmlFor="description">Description</label>
          <br />
          <textArea
            value={projectDesc}
            id="description"
            type="text"
            onChange={handleChange}
          />
          <br />
          <button type="submit" className="addBtn">
            Add
          </button>
        </form>
      </div>
    </div>
  );
}
